mapper.select:
SELECT COUNT(*) FROM REPORT WHERE 

		 REF_RUNO = #{userNo}  
    	 REF_TUNO = #{userNo}

mapper.update:
UPDATE NOTICE_BOARD SET 
	 NOTICE_BOARD_TITLE = #{noticeBoardTitle},
	 NOTICE_BOARD_CONTENT = #{noticeBoardContent}			
WHERE NOTICE_BOARD_NO = #{noticeBoardNo}

mapper.select:
SELECT COUNT(*) FROM MEMBER WHERE USER_LEVEL = #{type}
AND
USER_NAME LIKE CONCAT(CONCAT('%',#{keyword}),'%') USER_ID LIKE CONCAT(CONCAT('%',#{keyword}),'%')

SELECT REPORT_NO, REF_RUNO, REF_TUNO, M.USER_NAME AS R_NAME, M2.USER_NAME AS T_NAME, REPORT_CONTENT, REPORT_STATUS, REPORT_RESULT, REPORT_TYPE, REPORT_DATE FROM REPORT R LEFT JOIN MEMBER M ON R.REF_RUNO = M.USER_NO LEFT JOIN MEMBER M2 ON R.REF_TUNO = M2.USER_NO WHERE
REF_RUNO = #{userNo} REF_TUNO = #{userNo}
ORDER BY REPORT_DATE DESC;

mapper.update:
UPDATE MEMBER SET 
	 STATUS = #{status}		
WHERE USER_NO = #{userNo}
